sys_variable_value.value:
(function execute(inputs, outputs) {
  var depGr = new GlideRecord('u_aarons_department');
  depGr.get(inputs.department_sys_id);
  var rmDepGr = depGr.u_parent_department.getRefRecord();
  gs.info('rmDepGr:: ' + rmDepGr.getValue('name'))
  var adDepGr = rmDepGr.u_parent_department.getRefRecord();
  gs.info('adDepGr:: ' + adDepGr.getValue('name'))
  outputs.rm = searchForHead('Regional Manager', rmDepGr.getUniqueValue())
  outputs.ad = searchForHead('Regional Director', adDepGr.getUniqueValue())
})(inputs, outputs);

function searchForHead(title, depSysId){
  var userGr = new GlideRecord('sys_user');
  userGr.addQuery('title', title);
  gs.info('search for title:: ' + title);
  userGr.addQuery('department', depSysId);
  userGr.addActiveQuery();
  userGr.setLimit(1);
  userGr.query();
  userGr.next();
  return userGr;
}

